    //         this.getBooks();
    //     })
    //     .catch(err => {
    //         console.log("This is the error", err);
    //     })
    // }




    render() {
        return (
            <div>
                <Header />
                <Nav /> 
                <div>
                {this.state.savedBooks.length ? (
                    <BookList 
                    bookState={this.state.books}
                    deleteGoogleBook={this.deleteGoogleBook}
                    >
                    </BookList>
                 ) : (
                    <div>
                        <hr/>
                    <p style={{fontStyle: "italic"}}>No results to display</p>
                    </div>
                )}

                </div>
            </div>
        )
    }
}

export default Saved
