import React, { Component } from "react"
import Header from "../components/Header/header"
import Nav from "../components/Nav/nav"
import API from "../utils/API"
import BookList from "../components/SaveBooks/saveBooks"


class Saved extends Component {
    state = {
        savedBooks: []
    }

    componentDidMount = () => {
        this.getBooks()
    }

    deleteGoogleBook = selectedBook => {
        API.deleteBook( selectedBook.id )
        .then(res => {
            console.log("Deleted");
            this.getBooks();
        })
        .catch(err => {
            console.log(err);
        })
    }

    getBooks = () => {
        API.getBooks()
        .then(res => {
            this.setState({
                savedBooks: res.data
            })
            console.log(res);
        })
        .catch(err => {
            console.log(err);
        })
    }


    render() {
        return (

            <div>
                <Header/>
                <Nav/>
                {this.state.savedBooks.length ? (
                    <BookList
                    bookState={this.state.savedBooks}
                    deletGoogleBook={this.deleteGoogleBook}
                    ></BookList>

                ) : (
                    <h5>No Saved Books</h5>
                )}


            </div>


        )
    }



}

export default Saved
